import bcrypt from "bcrypt";
import jwt from "jsonwebtoken";

const Helpers = {
  hassPassword: async pass => {
    const saltRounds = 10;
    return await bcrypt.hash(pass, saltRounds);
  },
  generateToken: userId => {
    const token = jwt.sign({ userId }, process.env.JWT_SECRET, {
      expiresIn: "7 days"
    });
    return token;
  },
  getUserId: () => {}
